import { Component } from '@angular/core';
import { CounterApiService, CounterResponse } from './services/counter-api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'CounterFront';
  counterValue: number | null = null;

  constructor(private counterApiService: CounterApiService) {}

  mostrarContador(): void {
    this.counterApiService.getValue().subscribe(
      (response: CounterResponse) => {
        this.counterValue = response.value;
      },
      error => {
        console.error('Error al obtener el contador:', error);
      }
    );
  }
}
<h1>¡Hola, Angular!</h1>

<div class="button-container">
  <app-button action="increment" label="Incrementar"></app-button>
  <app-button action="decrement" label="Decrementar"></app-button>
  <app-button action="reset" label="Resetear"></app-button>
</div>

<button class="show-counter-btn" (click)="mostrarContador()">Mostrar Contador</button>

<div *ngIf="counterValue !== null" class="counter-value">
  Valor del contador: {{ counterValue }}
</div>
